delimiter ;;
CREATE PROCEDURE `getThongTinPhanCongChiTiet`(in _maNVPC int, in _maDA int)
BEGIN
	select phancong.*, duan.TenDA, duan.Phong, nhanvien.HoTen, nhanvien.MaPB
    from phancong, nhanvien, duan
    where phancong.MaDA = duan.MaDA and phancong.MaNVPC = nhanvien.MaNV
    and phancong.MaDA = _maDA and phancong.MaNVPC = _maNVPC;
END
;;
